-- Adds master grocery catalog, seeds defaults, and locks writes via RLS.
CREATE TABLE "master_grocery_catalog" (
    "id" TEXT NOT NULL,
    "name" TEXT NOT NULL,
    "category" TEXT NOT NULL,
    "default_unit" TEXT,
    "image_url" TEXT,
    "default_quantity" INTEGER,
    "created_at" TIMESTAMP(3) NOT NULL DEFAULT CURRENT_TIMESTAMP,
    "updated_at" TIMESTAMP(3) NOT NULL,
    CONSTRAINT "master_grocery_catalog_pkey" PRIMARY KEY ("id")
);

ALTER TABLE "shopping_items" ADD COLUMN "catalog_item_id" TEXT;

ALTER TABLE "shopping_items"
    ADD CONSTRAINT "shopping_items_catalog_item_id_fkey"
    FOREIGN KEY ("catalog_item_id") REFERENCES "master_grocery_catalog"("id")
    ON DELETE SET NULL ON UPDATE CASCADE;

CREATE INDEX "master_grocery_catalog_name_idx" ON "master_grocery_catalog"("name");
CREATE INDEX "master_grocery_catalog_category_idx" ON "master_grocery_catalog"("category");
CREATE INDEX "shopping_items_catalog_item_id_idx" ON "shopping_items"("catalog_item_id");

